import React from "react";
import { connect } from "react-redux";

import Header from "../components/Header/Header.jsx";

import album_1 from "../assets/img/album_1.png";
import left_arrow from "../assets/img/left-arrow.png";
import last_page from "../assets/img/last_page.png";
import next_page from "../assets/img/next_page.png";
import last_page_gray from "../assets/img/last_page_gray.png";
import next_page_gray from "../assets/img/next_page_gray.png";

import { updateSearchResult } from "../actions";

class App extends React.Component {
  state = {
    selectedTrack: "",
    all_tracks: "",
    current_track_page: 1,
  };

  componentDidUpdate(prevProps) {}

  loadMoreTracks() {
    // const search_value = event.target.value;
    // var Spotify = require('spotify-web-api-js');
    // var spotifyApi = new Spotify();
    // spotifyApi.setAccessToken('BQBpmXCRww0_J8QihEuhgG23uOcSJJv3M5JjIZln2uLxEsKbHF4siuIgvoBdxNf4cyUqGmhV0-N_SYAMFic');
    // spotifyApi.searchTracks(search_value, {limit: 8})
    // .then(
    //   function(data) {
    //     // console.log(data);
    //     if(data.length === 0) {
    //       data.result_flag = 0;
    //       this.props.updateSearchResult(data);
    //     }
    //     else {
    //       data.result_flag = 1;
    //       this.props.updateSearchResult(data);
    //     }
    //   }.bind(this),
    //   function(err) {
    //     // console.log(err);
    //     if(search_value === '') {
    //       err.result_flag = 0;
    //       this.props.updateSearchResult(err);
    //     }
    //     else {
    //       err.result_flag = 2;
    //       this.props.updateSearchResult(err);
    //     }
    //   }.bind(this)
    // );
  }

  handleTrackClick(track) {
    this.setState({ selectedTrack: track });
  }

  render() {
    const { selectedTrack } = this.state;
    const { result_flag, tracks_data, tracks_currentPage } = this.props;

    const album_bg_div_class =
      result_flag === 0
        ? "bg_fullsize album_bg_div"
        : "bg_smallsize album_bg_div";
    const album_bg_class =
      result_flag === 0
        ? "bg_image_fullsize album_bg"
        : "bg_image_smallsize album_bg";
    const tracks =
      tracks_data === "" ? undefined : tracks_data[tracks_currentPage].tracks;

    return (
      <div>
        <div className={album_bg_div_class}>
          <img
            className={album_bg_class}
            src={
              selectedTrack === "" || selectedTrack.album.images[0] === undefined ? album_1 : selectedTrack.album.images[0].url
            }
            alt="album_1"
          />
        </div>

        <Header />

        {tracks !== undefined && (
          <div className="content">
            <div>
              <div className="album_backshadow" />
              <div className="album_selected">
                {selectedTrack === "" ?
                  <img
                    className="album_selected_cover"
                    src={album_1}
                    alt="album_1"
                  /> :

                  selectedTrack.album.images[0] === undefined
                  ? <div className="album_selected_cover" style={{"display": "inline-block", "backgroundColor": "gray"}}/>
                  : <img
                  className="album_selected_cover"
                  src={selectedTrack.album.images[0].url
                  }
                  alt="album_1"
                />
                }
              </div>
            </div>
            <div className="search_content">
              <div className="search_result">
                <div className="search_result_title">
                  <img
                    className="left_arrow"
                    src={left_arrow}
                    alt="left_arrow"
                    onClick={() =>
                      this.props.updateSearchResult({ result_flag: 0 })
                    }
                  />
                  <span className="search_result_title_text">
                    Search result
                  </span>
                </div>
                <div className="search_result_content">
                  {tracks.items.length !== 0 &&
                  <div>
                    <div className="search_result_item">Tracks</div>
                    {tracks.items.map((track) => {
                      return (
                        <div
                          className="track_row"
                          key={track.id}
                          onClick={() => this.handleTrackClick(track)}
                        >
                          <span className="helper" />
                          {track.album.images[2] === undefined
                          ? <div className="track_row_cover" style={{"display": "inline-block", "width": "30px", "height": "30px", "backgroundColor": "gray"}}/>
                          : <img
                            className="track_row_cover"
                            src={track.album.images[2].url}
                            width={30}
                          />}
                          <span className="track_row_name">{track.name}</span>
                        </div>
                      );
                    })}
                    <div className="search_result_operations">
                      <img
                        src={
                          tracks.previous === null ? last_page_gray : last_page
                        }
                      />
                      <span className="search_result_current_page">
                        {tracks_currentPage}
                      </span>
                      <img
                        src={tracks.next === null ? next_page_gray : next_page}
                      />
                    </div>
                  </div>}
                  <div style={{ height: "60px" }} />
                </div>
              </div>
              <div className="sidebar"></div>
            </div>
          </div>
        )}
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  result_flag: state.searchResult.result_flag,
  tracks_data: state.searchResult.tracks_data,
  tracks_currentPage: state.searchResult.tracks_currentPage,
});

const mapDispatchToProps = (dispatch) => ({
  updateSearchResult: (result) => dispatch(updateSearchResult(result)),
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(App);
