import React from "react";
import { connect } from "react-redux";

import Header from "../components/Header/Header.jsx";

import album_1 from "../assets/img/album_1.png";
import left_arrow from "../assets/img/left-arrow.png";

class App extends React.Component {

  componentDidUpdate() {
  }

  render() {
    const album_bg_div_class = this.props.searchResult_flag === 0 ? "bg_fullsize album_bg_div" : "bg_smallsize album_bg_div"
    const album_bg_class = this.props.searchResult_flag === 0 ? "bg_image_fullsize album_bg" : "bg_image_smallsize album_bg"
    return (
      <div>
        <div className={album_bg_div_class}>
          <img className={album_bg_class} src={album_1} alt="album_1" />
        </div>

        <Header />
        <div className="content">
          <div>
            <div className="album_backshadow" />
            <div className="album_selected">
              <img className="album_selected_cover" src={album_1} alt="album_1" />
            </div>
          </div>
          <div className="search_content">
            <div className="search_result">
              <div className="search_result_title">
                <img className="left_arrow" src={left_arrow} alt='left_arrow' />
                <span className="search_result_title_text">Search result</span>
              </div>
            </div>
            <div className="sidebar">

            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  searchResult_flag: state.searchResult.result_flag,
  // search_result: state.searchResult.result,
});

const mapDispatchToProps = (dispatch) => ({});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(App);
