const searchResult = (state = {success: 0, result: ''}, action) => {
  switch (action.type) {
    case 'UPDATE_SEARCH_RESULT':
      state.success = action.success;
      state.result = action.result;
      return state
    default:
      return state
  }
}

export default searchResult
