import React from "react";
import { connect } from 'react-redux'
import axios from "axios";
// import PropTypes from "prop-types";

import avatar from '../../assets/img/avatar.png';
import search_icon from '../../assets/img/search.png';
import menu_icon from '../../assets/img/menu.png';

import { updateSearchResult } from '../../actions';

var Spotify = require('spotify-web-api-js');
var spotifyApi = new Spotify();
spotifyApi.setAccessToken('BQCL2wd03snvyN-k5qb583ZfLjG7BnSwcEC7pthZh1v0-u7ttV33Pph_AAJh2hYfQEWH_mEWpVDSbzeyLY8');

class Header extends React.Component {

  componentWillMount() {
    this.getToken();
  }

  getToken() {

    // var myHeaders = new Headers();

    // myHeaders.append('Content-Type', 'application/x-www-form-urlencoded')
    // myHeaders.append('Authorization', 'Basic YzY2ZTY5MTcyY2JkNGQ0N2E2ZmVjZTcwMjJkYThmMTU6NDNlNmY4ZWE1ZjZmNGMxMGE5ZTBmOTZiMDBjMmQ2OTM=')

    // const myInit = {
    //   method: 'POST',
    //   headers: myHeaders,
    //   body: JSON.stringify({
    //     grant_type: "client_credentials"
    //   })
    // }

    // // const proxyurl = "https://cors-anywhere.herokuapp.com/";
    // const myRequest = new Request('https://accounts.spotify.com/api/token', myInit)

    // fetch(myRequest)
    // .then(response =>
    //   console.log(response)
    // )



    var request = require('request');
    var authOptions = {
      url: 'https://accounts.spotify.com/authorize?client_id=c66e69172cbd4d47a6fece7022da8f15&response_type=code&redirect_uri=https%3A%2F%2Fexample.com%2Fcallback&scope=user-read-private%20user-read-email&state=34fFs29kd09'
    };
    request.get(authOptions, function(error, response, body) {
      console.log(response)
    }.bind(this));
  }

  handleSearch(event) {
    const search_value = event.target.value;
    spotifyApi.searchTracks(search_value, {limit: 8})
    .then(
      function(data) {
        let result = {
          tracks_data: {1: data},
          tracks_currentPage: 1
        };
        // console.log(data);
        if(data.length === 0) {
          result.result_flag = 0;
          this.props.updateSearchResult(result);
        }
        else {
          result.result_flag = 1;
          this.props.updateSearchResult(result);
        }
      }.bind(this),
      function(err) {
        // console.log(err);
        let result = {
          tracks_data: '',
          tracks_currentPage: 0
        };
        if(search_value === '') {
          result.result_flag = 0;
          this.props.updateSearchResult(result);
        }
        else {
          result.result_flag = 2;
          this.props.updateSearchResult(result);
        }
      }.bind(this)
    );
  }

  render() {
    return (
      <header>
        <div>
          <img className="avatar" src={avatar} alt="avatar" />
          <img className="search_icon" src={search_icon} alt="search" />
          <input className="search_input" type="text" name="spotify_search" placeholder="Search songs, artists..." onChange={event => this.handleSearch(event)}></input>
        </div>
        <div>
          <span className="helper" />
          <span className="header_item">Premium</span>
          <span className="header_item">Help</span>
          <span className="header_item">Download</span>
          <img className="menu_icon" src={menu_icon} alt="menu" />
        </div>
      </header>
    );
  }
}

