import React from "react";
import { connect } from "react-redux";

import Header from "../components/Header/Header.jsx";

import album_1 from "../assets/img/album_1.png";
import left_arrow from "../assets/img/left-arrow.png";

import { updateSearchResult } from '../actions';

class App extends React.Component {

  componentDidUpdate() {
  }

  render() {
    const album_bg_div_class = this.props.searchResult_flag === 0 ? "bg_fullsize album_bg_div" : "bg_smallsize album_bg_div"
    const album_bg_class = this.props.searchResult_flag === 0 ? "bg_image_fullsize album_bg" : "bg_image_smallsize album_bg"
    const tracks = this.props.searchResult_tracks
    return (
      <div>
        <div className={album_bg_div_class}>
          <img className={album_bg_class} src={album_1} alt="album_1" />
        </div>

        <Header />

        {tracks !== undefined &&
        <div className="content">
          <div>
            <div className="album_backshadow" />
            <div className="album_selected">
              <img className="album_selected_cover" src={album_1} alt="album_1" />
            </div>
          </div>
          <div className="search_content">
            <div className="search_result">
              <div className="search_result_title">
                <img className="left_arrow" src={left_arrow} alt='left_arrow' onClick={() => this.props.updateSearchResult({result_flag: 0})}/>
                <span className="search_result_title_text">Search result</span>
              </div>
              <div className="search_result_content">
                {this.props.searchResult_tracks.items.map(track => {
                  console.log(track);
                  return (
                    <div className="track_row" key={track.id}>
                      <span className="helper" />
                      <img className="track_row_cover" src={track.album.images[2].url} width={30} />
                      <span className="track_row_name">{track.name}</span>
                    </div>
                  )
                })}
              </div>
            </div>
            <div className="sidebar">

            </div>
          </div>
        </div>}
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  searchResult_flag: state.searchResult.result_flag,
  searchResult_tracks: state.searchResult.tracks
});

const mapDispatchToProps = (dispatch) => ({
  updateSearchResult: result => dispatch(updateSearchResult(result))
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(App);
