import React from "react";
import logo from "./logo.svg";
import "./App.css";
// token: BQAuSJqObCl9n63GtIKYG2IJ4DFHp47PiqdIKyVlAFsUW9AWA2s0hNR4k-oLnBdnt4JQmtUwhkQKiXTaL1E

// curl -d grant_type=client_credentials -H "Authorization: Basic YzY2ZTY5MTcyY2JkNGQ0N2E2ZmVjZTcwMjJkYThmMTU6NDNlNmY4ZWE1ZjZmNGMxMGE5ZTBmOTZiMDBjMmQ2OTM=" -X POST https://accounts.spotify.com/api/token

function App() {

  var Spotify = require('spotify-web-api-js');
  var spotifyApi = new Spotify();
  spotifyApi.setAccessToken('BQAuSJqObCl9n63GtIKYG2IJ4DFHp47PiqdIKyVlAFsUW9AWA2s0hNR4k-oLnBdnt4JQmtUwhkQKiXTaL1E');
  // spotifyApi.setPromiseImplementation(Q);

  spotifyApi.getArtistAlbums('BQAuSJqObCl9n63GtIKYG2IJ4DFHp47PiqdIKyVlAFsUW9AWA2s0hNR4k-oLnBdnt4JQmtUwhkQKiXTaL1E')
  .then(function(data) {
    console.log('Artist albums', data);
  }, function(err) {
    console.error(err);
  });

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
