import React from "react";
import { connect } from "react-redux";

import Header from "../components/Header/Header.jsx";

import album_1 from "../assets/img/album_1.png";
import left_arrow from "../assets/img/left-arrow.png";
import last_page from "../assets/img/last_page.png";
import next_page from "../assets/img/next_page.png";
import last_page_gray from "../assets/img/last_page_gray.png";
import next_page_gray from "../assets/img/next_page_gray.png";
import play from "../assets/img/play.png";
import pause from "../assets/img/pause.png";
import heart from "../assets/img/heart.png";

import { updateSearchResult } from "../actions";

let playing;

class App extends React.Component {
  state = {
    selected_track: "",
    all_tracks: "",
    current_track_page: 1,
    player_current_time: 0,
    player_current_width: 0,
    playing: '',
    paused: false
  };

  componentDidUpdate() {
    // var vid = document.getElementById("audio_player");
    // if(vid !== null) console.log(vid.currentTime);
  }

  previousTracks() {
    const tracks_data = this.props.tracks_data;
    const tracks_currentPage = this.props.tracks_currentPage;
    const result = {
      tracks_data: tracks_data,
      tracks_currentPage: tracks_currentPage - 1,
      result_flag: 1,
    };
    this.props.updateSearchResult(result);
  }

  loadMoreTracks(tracks) {
    var request = require("request");
    var authOptions = {
      url: tracks.next,
      headers: {
        Authorization: "Bearer " + this.props.token,
      },
    };
    request.get(
      authOptions,
      function(error, response, body) {
        const tracks_data = this.props.tracks_data;
        const tracks_currentPage = this.props.tracks_currentPage;
        tracks_data[tracks_currentPage + 1] = JSON.parse(body);
        const result = {
          tracks_data: tracks_data,
          tracks_currentPage: tracks_currentPage + 1,
          result_flag: 1,
        };
        this.props.updateSearchResult(result);
      }.bind(this)
    );
  }

  handleTrackClick(track) {
    clearInterval(playing);
    this.setState({
      player_current_width: 0,
      selected_track: track
    });
    const aud_bg = document.getElementsByClassName("player_bg");
    const sec_width = aud_bg[0].clientWidth/30;
    if (track.preview_url !== null) {
      let i = 1;
      playing = setInterval(function() {
        if(this.state.paused === false) {
          this.setState({
            player_current_time: Math.floor(i/100),
            player_current_width: (i/100) * sec_width
          });
          i++;
          if(i === 3000) clearInterval(playing);
        }
      }.bind(this), 10);
    }
  }

  handlePause() {
    this.setState({paused: true});
    var vid = document.getElementById("audio_player");
    vid.pause();
  }

  handlePlay() {
    this.setState({paused: false});
    var vid = document.getElementById("audio_player");
    vid.play();
  }

  render() {
    const { selected_track, player_current_width } = this.state;
    const { result_flag, tracks_data, tracks_currentPage } = this.props;

    const album_bg_div_class =
      result_flag === 0
        ? "bg_fullsize album_bg_div"
        : "bg_smallsize album_bg_div";
    const album_bg_class =
      result_flag === 0
        ? "bg_image_fullsize album_bg"
        : "bg_image_smallsize album_bg";
    const tracks =
      tracks_data === "" ? undefined : tracks_data[tracks_currentPage].tracks;

    return (
      <div>
        <div className={album_bg_div_class}>
          <img
            className={album_bg_class}
            src={
              selected_track === "" ||
              selected_track.album.images[0] === undefined
                ? album_1
                : selected_track.album.images[0].url
            }
            alt="album_1"
          />
          <div className="player_bg">
            <div className="player_progress" style={{"width": player_current_width}} />
          </div>
        </div>

        <Header />

        {selected_track !== "" && selected_track.preview_url !== null && (
          <audio style={{"display": "none"}} id="audio_player" src={selected_track.preview_url} controls autoPlay />
        )}

        {tracks !== undefined && (
          <div className="content">
            <div>
              <div className="album_backshadow" />
              <div className="album_selected">
                {selected_track === "" ? (
                  <img
                    className="album_selected_cover"
                    src={album_1}
                    alt="album_1"
                  />
                ) : selected_track.album.images[0] === undefined ? (
                  <div
                    className="album_selected_cover"
                    style={{ display: "inline-block", backgroundColor: "gray" }}
                  />
                ) : (
                  <img
                    className="album_selected_cover"
                    src={selected_track.album.images[0].url}
                    alt="album_1"
                  />
                )}
              </div>
            </div>
            {(selected_track !== "" && selected_track.preview_url !== null) &&
            <div className="audio_player">
              <div style={{"display": "flex"}}>
                <img
                  className="audio_player_cover"
                  src={selected_track.album.images[0].url}
                  alt="album_1"
                />
                <div className="audio_player_info">
                  <div style={{
                    "marginTop": "15px",
                    "color": "#353540"
                  }}>{selected_track.name}</div>
                  <div
                  style={{
                    "marginTop": "11px",
                    "color": "#646c73"
                  }}>{selected_track.artists[0].name}</div>
                </div>
              </div>
              <div style={{"display": "flex", "alignItems": "center"}}>
                <div style={{
                  "color": "#2b3143",
                  "font-size": "11px"
                }}>0:{this.state.player_current_time} / 0:30</div>
                {this.state.paused === true ?
                <img style={{"marginLeft": "72px", "cursor": "pointer"}} src={play} alt="play" width={32} onClick={() => this.handlePlay()}/> :
                <img style={{"marginLeft": "72px", "cursor": "pointer"}} src={pause} alt="pause" width={32} onClick={() => this.handlePause()}/>}
                <img style={{"marginLeft": "65px", "cursor": "pointer"}} src={heart} alt="heart"/>
              </div>
            </div>}
            <div className="search_content">
              <div className="search_result">
                <div className="search_result_title">
                  <img
                    className="left_arrow"
                    src={left_arrow}
                    alt="left_arrow"
                    onClick={() =>
                      this.props.updateSearchResult({
                        result_flag: 0,
                        tracks_data: "",
                      })
                    }
                  />
                  <span className="search_result_title_text">
                    Search result
                  </span>
                </div>
                <div className="search_result_content">
                  {tracks.items.length !== 0 && (
                    <div>
                      <div className="search_result_item">Tracks</div>
                      {tracks.items.map((track) => {
                        return (
                          <div
                            className="track_row"
                            key={track.id}
                            onClick={() => this.handleTrackClick(track)}
                          >
                            <span className="helper" />
                            {track.album.images[2] === undefined ? (
                              <div
                                className="track_row_cover"
                                style={{
                                  display: "inline-block",
                                  width: "30px",
                                  height: "30px",
                                  backgroundColor: "gray",
                                }}
                              />
                            ) : (
                              <img
                                className="track_row_cover"
                                src={track.album.images[2].url}
                                width={30}
                                alt="track_row_cover"
                              />
                            )}
                            <span className="track_row_name">{track.name}</span>
                          </div>
                        );
                      })}
                      <div className="search_result_operations">
                        <img
                          src={
                            tracks.previous === null
                              ? last_page_gray
                              : last_page
                          }
                          style={{
                            cursor: tracks.previous === null ? "" : "pointer",
                          }}
                          onClick={() => this.previousTracks()}
                          alt="last"
                        />
                        <span className="search_result_current_page">
                          {tracks_currentPage}
                        </span>
                        <img
                          src={
                            tracks.next === null ? next_page_gray : next_page
                          }
                          style={{
                            cursor: tracks.next === null ? "" : "pointer",
                          }}
                          onClick={() => this.loadMoreTracks(tracks)}
                          alt="next"
                        />
                      </div>
                    </div>
                  )}
                  <div style={{ height: "60px" }} />
                </div>
              </div>
              <div className="sidebar"></div>
            </div>
          </div>
        )}
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  token: state.token,
  result_flag: state.searchResult.result_flag,
  tracks_data: state.searchResult.tracks_data,
  tracks_currentPage: state.searchResult.tracks_currentPage,
});

const mapDispatchToProps = (dispatch) => ({
  updateSearchResult: (result) => dispatch(updateSearchResult(result)),
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(App);
