import React from "react";
import { connect } from "react-redux";

import Header from "../components/Header/Header.jsx";

import album_1 from "../assets/img/album_1.png";

class App extends React.Component {

  componentDidUpdate() {
  }

  render() {
    const album_bg_div_class = this.props.searchResult_flag === 0 ? "bg_fullsize album_bg_div" : "bg_smallsize album_bg_div"
    return (
      <div>
        <Header />
        <div className={album_bg_div_class}>
          <img className="album_bg" src={album_1} alt="album_1" />
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  searchResult_flag: state.searchResult.result_flag,
  // search_result: state.searchResult.result,
});

const mapDispatchToProps = (dispatch) => ({});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(App);
