import React from "react";
import axios from "axios";
// import PropTypes from "prop-types";

import avatar from '../../assets/img/avatar.png';
import search_icon from '../../assets/img/search.png';
import menu_icon from '../../assets/img/menu.png';

class Header extends React.Component {

  state = {
  }

  componentWillMount() {
    this.getToken();
  }

  getToken() {
    // var myHeaders = new Headers();

    // myHeaders.append('Content-Type', 'application/x-www-form-urlencoded')
    // myHeaders.append('Authorization', 'Basic YzY2ZTY5MTcyY2JkNGQ0N2E2ZmVjZTcwMjJkYThmMTU6NDNlNmY4ZWE1ZjZmNGMxMGE5ZTBmOTZiMDBjMmQ2OTM=')

    // const myInit = {
    //   method: 'POST',
    //   headers: myHeaders,
    //   body: JSON.stringify({
    //     grant_type: "client_credentials"
    //   })
    // }

    // // const proxyurl = "https://cors-anywhere.herokuapp.com/";
    // const myRequest = new Request('https://accounts.spotify.com/api/token', myInit)

    // fetch(myRequest)
    // .then(response =>
    //   console.log(response)
    // )
    axios({
        url: 'https://accounts.spotify.com/api/token',
        method: 'post',
        params: {
          "grant_type" : 'client_credentials',
        },
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
          'Authorization': 'Basic YzY2ZTY5MTcyY2JkNGQ0N2E2ZmVjZTcwMjJkYThmMTU6NDNlNmY4ZWE1ZjZmNGMxMGE5ZTBmOTZiMDBjMmQ2OTM='
        },
    })
    .then(function(response) {
      console.log(response);
    })
    .catch(function(error) {
      console.log(error)
    });
  }

  handleSearch(event) {

    var Spotify = require('spotify-web-api-js');
    var spotifyApi = new Spotify();
    spotifyApi.setAccessToken('BQCEgpiTClOyVpfZ9PL6mKYLndRYyetBeyzMPuiUHkdDacnTSMUQegooZdFMlq5u5dcN1SZmYQLtqMmN2KA');
    // spotifyApi.setPromiseImplementation(Q);

    spotifyApi.searchTracks(event.target.value)
    .then(function(data) {
      console.log('Search by "Love"', data);
    }, function(err) {
      console.error(err);
    });
  }

  render() {
    return (
      <header>
        <div>
          <img className="avatar" src={avatar} alt="avatar" />
          <img className="search_icon" src={search_icon} alt="search" />
          <input className="search_input" type="text" name="spotify_search" placeholder="Search songs, artists..." onChange={event => this.handleSearch(event)}></input>
        </div>
        <div>
          <span className="helper" />
          <span className="header_item">Premium</span>
          <span className="header_item">Help</span>
          <span className="header_item">Download</span>
          <img className="menu_icon" src={menu_icon} alt="menu" />
        </div>
      </header>
    );
  }
}

// Header.propTypes = {
